import {
  Component,
  ContentChild,
  Input,
  OnInit,
  TemplateRef,
} from "@angular/core";

@Component({
  selector: "app-table",
  templateUrl: "./table.component.html",
  styleUrls: ["./table.component.scss"],
})
export class TableComponent implements OnInit {
  @Input() data!: any[];
  @ContentChild("headers") headers: TemplateRef<any> | undefined;
  @ContentChild("rows") rows: TemplateRef<any> | undefined;
  public query: any = '';
  constructor() {}

  ngOnInit() {}
}

                        <input type="text" class="form-control mb-4" placeholder="Search" [(ngModel)]="query" id="listSearch">
                       <table class="table">
                            <thead class=" text-primary">
                              <ng-container
                                *ngTemplateOutlet="
                                  headers || defaultHeaderTemplate;
                                  context: { $implicit: data }
                                "
                              ></ng-container>

                            </thead>
                            <tbody>

                              <tr *ngFor="let row  of data | filter: query" >
                                <ng-container
                                      *ngTemplateOutlet="
                                        rows || defaultRowTemplate;
                                        context: { $implicit: row }
                                      "
                              ></ng-container>
                              </tr>
                          </tbody>
                        </table>

                            <!-- If no template is provided use keys as headers and display all values -->
                            <ng-template #defaultHeaderTemplate let-data>
                              <th *ngFor="let header of data[0] | keyvalue">{{ header.key }}</th>
                            </ng-template>
                            <ng-template #defaultRowTemplate let-row>
                              <td *ngFor="let row of row | keyvalue">{{ row.value }}</td>
                            </ng-template>
