import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthService } from 'app/auth/auth.service';
import { RoomService } from 'app/services/room.service';
import { ToastrService } from 'app/services/Toastr.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-user-profile',
  templateUrl: './user-profile.component.html',
  styleUrls: ['./user-profile.component.css']
})
export class UserProfileComponent implements OnInit {

  profileModel: FormGroup;
  userTypes: unknown[] =[];
  roomsAvailable: unknown[] =[];

  constructor(private auth: AuthService, private roomService: RoomService, private fb: FormBuilder, private toastr: ToastrService) { }

  ngOnInit() {
    const id =  this.auth.getUserID;
    this.getProfile(id);
    this.getUserTypes()

    this.profileModel = this.fb.group({
      Username: [{value: '',disabled: true}, Validators.required,],
      FirstName: ['', Validators.required],
      Surname: ['', Validators.required],
    });
  }

  onSubmit() {
    // this.courses.UpdateUser(this.profileModel.value,this.auth.getUserID).subscribe(result =>{
    //   console.log(result);
    //   this.auth.setUserType(result.UserTypeId)
    //   this.toastr.showNotification("successfully updated!",1);

    // })
  }

  Book(room) {

    let book = {
      roomId: room.roomId,
      bookingId: room.bookId,
    }
    Swal.fire({
      title: 'Are you sure you want to cancel the booking?',
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#ff0000',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Yes, book it!'
    }).then((result) => {
      if (result.isConfirmed) {

        this.roomService.cancelBooking(book).subscribe((result) => {
          Swal.fire(
            'Booked!',
            'Room successfully cancelled.',
            'success'
          )
          const id =  this.auth.getUserID;
          this.getProfile(id);

        }, error => {

        })

      }
    })
  }

  getProfile(id: number) {
    this.roomService.getProfile(id).subscribe((profile:any) => {
      console.log(profile);
      this.roomsAvailable = profile.BookedRooms
      this.profileModel.patchValue({
        Username: profile.Username,
        FirstName: profile.FirstName,
        Surname: profile.Surname,
      })

    })
  }
  getUserTypes() {

    this.auth.getTypes().subscribe(res => {
      console.log(res);
      this.userTypes =  res;
    })
  }

}
<div class="main-content">
  <div class="container-fluid">
      <div class="row">
          <div class="col-md-8">
              <div class="card">
                  <div class="card-header card-header-danger">
                      <h4 class="card-title">Edit Profile</h4>
                      <p class="card-category">Complete your profile</p>
                  </div>
                  <div class="card-body">
                    <form [formGroup]="profileModel" autocomplete="off" (submit)="onSubmit()">

                          <div class="row">
                              <div class="col-md-6">
                                <mat-form-field class="example-full-width">
                                  <input matInput  formControlName="FirstName" placeholder="Fist Name" type="text">
                                </mat-form-field>
                              </div>
                              <div class="col-md-6">
                                <mat-form-field class="example-full-width">
                                  <input matInput  formControlName="Surname" placeholder="Last Name" type="text">
                                </mat-form-field>
                              </div>
                          </div>
                          <div class="row">
                            <div class="col-md-12">
                              <mat-form-field class="example-full-width">
                                <input matInput  formControlName="Username" placeholder="User Name" type="text">
                              </mat-form-field>
                            </div>
                        </div>

                          <button mat-raised-button type="submit" class="btn btn-danger pull-right">Update Profile</button>
                          <div class="clearfix"></div>
                      </form>
                  </div>
              </div>
              <div class="card" *ngFor="let room of roomsAvailable">
                <div class="card-header card-header-primary">
                  <h4 class="card-title">{{room.roomName}}</h4>
                  <p class="card-category">
                    <i class="material-icons text-danger">house</i>
                    <a href="#pablo">Room no: {{room.roomNumber}}</a>
                  </p>
                </div>
                <div class="card-body">
                  <span class="badge rounded-pill text-bg-warning">{{room.startDate | date}}</span> -
                  <span class="badge rounded-pill text-bg-warning">{{room.endDate | date }}</span>
                  <hr>
                  <h5 class="card-title">Amenities
                  </h5>
                  <ul *ngFor="let amenity of room.amenities ">
                    <li>{{amenity.Name}}</li>
                  </ul>
                  <div class="d-grid gap-2 d-md-flex justify-content-md-end">
                  <mat-chip color="primary" selected style="font-size: 16px; font-weight: bold;">Booked</mat-chip>
                  </div>

                  <div class="d-grid gap-2 d-md-flex justify-content-md-end">
                    <button mat-raised-button color="danger"  (click)="Book(room)" class="btn orange">Cancel Booking</button>
                </div>
                </div>

              </div>
          </div>
          <div class="col-md-4">
              <div class="card card-profile">>
                  <div class="card-body">
                      <h6 class="card-category text-gray">CEO / Co-Founder</h6>
                      <h4 class="card-title">Alec Thompson</h4>
                      <p class="card-description">
                          Don't be scared of the truth because we need to restart the human foundation in truth And I love you like Kanye loves Kanye I love Rick Owens’ bed design but the back is...
                      </p>
                      <a href="javascript:void(0)" class="btn btn-danger btn-round">Follow</a>
                  </div>
              </div>
          </div>
      </div>
  </div>
</div>
