import { Component, OnInit } from '@angular/core';
import { CentreService } from '../services/centre.service';

@Component({
  selector: 'app-registered-learners',
  templateUrl: './registered-learners.component.html',
  styleUrls: ['./registered-learners.component.scss']
})
export class RegisteredLearnersComponent implements OnInit {

  learners: any[] = [];
  public query: any = '';
  constructor(private centreService: CentreService) { }

  ngOnInit() {
    this.getLearners();
  }

  getLearners() {
    this.centreService.getLearners().subscribe(result => {
        this.learners =  result;
        console.log(this.learners)
    })
  }


  getUserTypeDesc(value) {
    return value == 1 ?  "Junior" : "Senior";
  }

}
<div class="main-content">
  <div class="container-fluid">
      <div class="row">
          <div class="col-md-12">
              <div class="card">
                <div class="card-header card-header-danger">
                  <h4 class="card-title ">Learners</h4>
                  <p class="card-category"> List of learners offered atJohannesbug Hotel</p>




              </div>
                  <div class="card-body">

                    <div class="form-group">
                      <input type="text" class="form-control mb-4" placeholder="Search" [(ngModel)]="query" id="listSearch">
                    </div>
                      <div class="table-responsive">
                          <table class="table">
                              <thead class=" text-primary">
                                  <th>
                                    FirstName
                                  </th>
                                  <th>
                                    Surname
                                  </th>
                                  <th>
                                    UserType
                                  </th>

                              </thead>
                              <tbody>

                                  <tr *ngFor="let user  of learners| filter: query" >
                                    <td>
                                      {{user.FirstName}}
                                    </td>
                                    <td >
                                      {{user.Surname}}

                                    </td>
                                      <td >
                                        {{getUserTypeDesc(user.UserTypeId)}}
                                    </td>
                                  </tr>
                              </tbody>
                          </table>
                      </div>
                  </div>
              </div>
          </div>
      </div>
  </div>
  </div>
